import React, { useState, useEffect, Component } from "react";
import axios from "axios";

const Component1 = () => {
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  let part = 1;

  const checkNotEmptyUndefinded = (value) => {
    if (value === undefined || value === null || value === "") {
      return false;
    }
    return true;
  };

  const checkObjectArrayString = (value) => {
    // to be removed
    if (typeof value === "object" && value.length > 0) {
      return true;
    }
    if (typeof value === "string") {
      return true;
    }
    return false;
  };

  const explodePart = (part) => {
    if (typeof part === "object") {
      console.log("parsing as an object");
      let element;
      for (const [key, value] of Object.entries(part)) {
        element = element + `<li><span>key: ${key} value ${value}</span></li>`;
      }
      return element;
    } else if (typeof part === "string") {
      console.log("parsing as a string");
      return "is string";
    } else if (typeof part === "array") {
      console.log("parsing as an array");
      return "is array";
    }
  };

  // {
  //   Object.keys(subjects).map((keyName, i) => (
  //     <li className="travelcompany-input" key={i}>
  //       <span className="input-label">
  //         key: {i} Name: {subjects[keyName]}
  //       </span>
  //     </li>
  //   ));
  // }

  let config = {
    method: "get",
    url: `http://localhost:8080/part?id=${part}`,
    headers: {},
  };
  useEffect(() => {
    axios(config)
      .then(function (response) {
        setData(response.data);
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // console.log("typeof", typeof data?.spec);
  // console.log("length", data?.spec.length);

  return (
    <>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div>Kind: {data?.kind}</div>
          <div>Spec: {explodePart(data?.spec)}</div>
          <div>Status:</div>
          <div>Metadata:</div>
          <div>APIVersion:</div>
          {/* {data?.map((item, index) => (
            <div key={index}>
              <h1>{item.name}</h1>
              <p>{item.description}</p>
            </div>
          ))} */}
        </div>
      )}
    </>
  );
};
