import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import { splitCamelCaseAndUppercase, isObject } from "../functions";

const Component1 = () => {
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);

  const param = useParams();
  const part = param.part;
  console.log("part:", part);

  // let part = 1;

  let config = {
    method: "get",
    url: `http://localhost:8080/part?id=${part}`,
    headers: {},
  };
  useEffect(() => {
    axios(config)
      .then(function (response) {
        setData(response.data);
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Create a function that takes an object and returns a table of its properties and values.
  const JsonTableComponent = (json) => {
    if (isObject(json)) {
      let table = [];
      for (let key in json) {
        if (json.hasOwnProperty(key)) {
          table.push(
            <tr key={key}>
              <td style={{ backgroundColor: "#EAE7E6" }}>
                <b>{splitCamelCaseAndUppercase(key)}</b>
              </td>
              <td>{JsonTableComponent(json[key])}</td>
            </tr>
          );
        }
      }
      return (
        <table className="table" border="1">
          <tbody>{table}</tbody>
        </table>
      );
    }
    return json;
  };

  return (
    <>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div className="tableContainer">
            <JsonTableComponent json={data} />
          </div>
        </div>
      )}
    </>
  );
};

export default Component1;
