import React, { useState, useEffect } from "react";
import axios from "axios";

const Component1 = () => {
  const [data, setData] = useState("");
  const [loading, setLoading] = useState(true);
  let part = 1;

  let config = {
    method: "get",
    url: `http://localhost:8080/part?id=${part}`,
    headers: {},
  };
  useEffect(() => {
    axios(config)
      .then(function (response) {
        setData(response.data);
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const isObject = (value) => {
    if (value === null) {
      return false;
    }
    return typeof value === "object";
  };

  // Create a function that takes an object and returns a table of its properties and values.
  const JsonTableComponent = (obj) => {
    let table = "";
    for (let val in obj) {
      if (isObject(obj[val])) {
        table += `<tr><td>${val}</td><td>${JsonTableComponent(
          obj[val]
        )}</td></tr>`;
      } else if (Array.isArray(obj[val])) {
        table += `<tr><td>${val}</td><td>${JsonTableComponent(
          obj[val]
        )}</td></tr>`;
      } else {
        table += `<tr><td>${val}</td><td>${obj[val]}</td></tr>`;
      }
    }
    table = `<table className='table'>${table}</table>`;
    return <div>{table}</div>;
  };

  // create a function that takes an object and returns nested divs of its properties and values.
  const JsonDivComponent = (obj) => {
    let div = "";
    for (let val in obj) {
      if (isObject(obj[val])) {
        div += `<div>${val}</div>${JsonDivComponent(obj[val])}`;
      } else if (Array.isArray(obj[val])) {
        div += `<div>${val}</div>${JsonDivComponent(obj[val])}`;
      } else {
        div += `<div>${val}</div>${obj[val]}`;
      }
    }
    div = `<div>${div}</div>`;
    return <div>{div}</div>;
  };

  return (
    <>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div>
            <JsonTableComponent data={data} />
            <JsonDivComponent data={data} />
          </div>
        </div>
      )}
    </>
  );
};

export default Component1;
