import React, { useState, useEffect } from "react";
import axios from "axios";

const Component1 = () => {
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  let part = 1;

  let config = {
    method: "get",
    url: `http://localhost:8080/part?id=${part}`,
    headers: {},
  };
  useEffect(() => {
    axios(config)
      .then(function (response) {
        // setData(response.data);

        setData({
          kind: "Service",
          spec: {
            selector: { matchLabels: {} },
            tlsSecret: {},
            previewUrl: { enabled: false },
            acmeProvider: { privateKeySecret: {} },
            type: "ClusterIP",
            ports: [{ name: "https", port: 443, protocol: "TCP" }],
            clusterIP: "10.43.0.1",
            sessionAffinity: "None",
            nodeSelector: {},
            securityContext: {
              capabilities: {},
              readOnlyRootFilesystem: false,
              allowPrivilegeEscalation: false,
              runAsUser: 0,
            },
            affinity: { podAntiAffinity: {} },
            config: { cepc: {} },
            regex_rewrite: {},
            add_response_headers: { "cache-control": {} },
          },
          status: { startTime: "0001-01-01T00:00:00Z", loadBalancer: {} },
          metadata: {
            uid: "e51dab73-52fd-40e1-94be-5a17b471034f",
            name: "kubernetes",
            labels: { provider: "kubernetes", component: "apiserver" },
            selfLink: "/api/v1/namespaces/default/services/kubernetes",
            namespace: "default",
            annotations: {
              "endpoints.kubernetes.io/last-change-trigger-time":
                "0001-01-01T00:00:00Z",
            },
            resourceVersion: "146",
            creationTimestamp: "2021-02-01T19:38:48Z",
          },
          apiVersion: "v1",
        });
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const isObject = (value) => {
    if (value === null) {
      return false;
    }
    return typeof value === "object";
  };

  // Create a function that takes an object and returns a table of its properties and values.
  const JsonTableComponent = (json) => {
    if (isObject(json)) {
      let table = [];
      for (let key in json) {
        if (json.hasOwnProperty(key)) {
          table.push(
            <tr key={key}>
              <td style={{ backgroundColor: "#EAE7E6" }}>
                <b>{splitCamelCaseAndUppercase(key)}</b>
              </td>
              <td>{JsonTableComponent(json[key])}</td>
            </tr>
          );
        }
      }
      return (
        <table className="table" border="1">
          <tbody>{table}</tbody>
        </table>
      );
    }
    return json;
  };

  // Create a function to split by camelCase and uppercase the first letter
  const splitCamelCaseAndUppercase = (str) => {
    const val = str.replace(/([A-Z])/g, " $1").trim();
    return val.charAt(0).toUpperCase() + val.slice(1);
  };

  return (
    <>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div className="tableContainer">
            <JsonTableComponent json={data} />
          </div>
        </div>
      )}
    </>
  );
};

export default Component1;
