import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { splitCamelCaseAndUppercase, isObject } from "../functions";
import Component2 from "./Component2";

const Component1 = () => {
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  const [part, setPart] = useState("");

  let config = {
    method: "get",
    url: `http://localhost:8080/len`,
    headers: {},
  };
  useEffect(() => {
    axios(config)
      .then(function (response) {
        setData(response.data);
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Show the index of the parts.
  const ServicesIndex = ({ data }) => {
    let value = [];
    for (let i = 0; i < data; i++) {
      value.push(
        <ul key={i}>
          <Link to={`/Component2/part?id=${i}`}>
            View details of service {i}
          </Link>
        </ul>
      );
    }
    return value;
  };

  // Function that takes an object and returns a table of its properties and values.
  const JsonTableComponent = (json) => {
    if (isObject(json)) {
      let table = [];
      for (let key in json) {
        if (json.hasOwnProperty(key)) {
          table.push(
            <tr key={key}>
              <td style={{ backgroundColor: "#EAE7E6" }}>
                <b>{splitCamelCaseAndUppercase(key)}</b>
              </td>
              <td>{JsonTableComponent(json[key])}</td>
            </tr>
          );
        }
      }
      return (
        <table className="table" border="1">
          <tbody>{table}</tbody>
        </table>
      );
    }
    return json;
  };

  return (
    <>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div>
            {part ? (
              <div>
                <div className="tableContainer">
                  <Component2 part={part} />
                </div>
              </div>
            ) : (
              <div>
                <ServicesIndex data={data} />
              </div>
            )}
          </div>
        </div>
      )}
    </>
  );
};

export default Component1;
