import React, { useState, useEffect } from "react";
import axios from "axios";

const Component1 = () => {
  const [data, setData] = useState({});
  const [loading, setLoading] = useState(true);
  let part = 1;

  let config = {
    method: "get",
    url: `http://localhost:8080/part?id=${part}`,
    headers: {},
  };
  useEffect(() => {
    axios(config)
      .then(function (response) {
        setData(response.data);
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const isObject = (value) => {
    if (value === null) {
      return false;
    }
    return typeof value === "object";
  };

  // Create a function that takes an object and returns a table of its properties and values.

  const JsonTableComponent = (json) => {
    if (isObject(json)) {
      let table = [];
      for (let key in json) {
        if (json.hasOwnProperty(key)) {
          table.push(
            <tr key={key}>
              <td>
                <b>{splitCamelCaseAndUppercase(key)}</b>
              </td>
              <td>{JsonTableComponent(json[key])}</td>
            </tr>
          );
          // } else {
          //   table.push(
          //     <tr key={key}>
          //       <td>{key}</td>
          //       <td>{json[key]} OK!</td>
          //     </tr>
          //   );
        }
      }
      return (
        <table className="table" border="1">
          <tbody>{table}</tbody>
        </table>
      );
    }
    return json;
  };

  // Create a function to split by camelCase.
  const splitCamelCaseAndUppercase = (str) => {
    const val = str.replace(/([A-Z])/g, " $1").trim();
    return val.charAt(0).toUpperCase() + val.slice(1);
  };

  function getDepth(obj) {
    if (!obj || obj.length === 0 || typeof obj !== "object") return 0;
    const keys = Object.keys(obj);
    let depth = 0;
    keys.forEach((key) => {
      let tmpDepth = getDepth(obj[key]);
      if (tmpDepth > depth) {
        depth = tmpDepth;
      }
    });
    return depth + 1;
  }

  if (data) {
    // console.log(getDepth(data));
    // console.log(splitCamelCase("readOnlyRootFilesystem"));
    console.log(data?.stringify());
  }

  return (
    <>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div className="tableContainer">
            <JsonTableComponent json={data} />
          </div>
        </div>
      )}
    </>
  );
};

export default Component1;
