import React, { useState, useEffect } from "react";
import axios from "axios";

const Component1 = () => {
  const [data, setData] = useState("");
  const [loading, setLoading] = useState(true);
  let part = 1;

  let config = {
    method: "get",
    url: `http://localhost:8080/part?id=${part}`,
    headers: {},
  };
  useEffect(() => {
    axios(config)
      .then(function (response) {
        setData(response.data);
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const isObject = (value) => {
    if (value === null) {
      return false;
    }
    return typeof value === "object";
  };

  // Create a function that takes an object and returns a table of its properties and values.
  const JsonTableComponent = (json) => {
    if (isObject(json)) {
      let table = [];
      for (let key in json) {
        if (json.hasOwnProperty(key)) {
          table.push(
            <tr key={key}>
              <td>{key}</td>
              <td>{JsonTableComponent(json[key])}</td>
            </tr>
          );
        } else {
          table.push(
            <tr key={key}>
              <td>{key}</td>
              <td>{json[key]} OK!</td>
            </tr>
          );
        }
      }
      return (
        <table className="table" border="1">
          <tbody>{table}</tbody>
        </table>
      );
    }
    return json;
  };

  // const getDepth = (obj) => {
  //   var depth = 0;
  //   if (obj.children) {
  //     obj.children.forEach(function (d) {
  //       var tmpDepth = getDepth(d);
  //       if (tmpDepth > depth) {
  //         depth = tmpDepth;
  //       }
  //     });
  //   }
  //   return 1 + depth;
  // };

  var getDepth = function (branch) {
    if (!branch.children) {
      return 1;
    }
    return branch.children.reduce(function (c, b) {
      return c + getDepth(b);
    }, 0);
  };

  if (data) {
    console.log(getDepth(data));
  }

  return (
    <>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div className="tableContainer">
            <JsonTableComponent json={data} />
          </div>
        </div>
      )}
    </>
  );
};

export default Component1;
